INSERT INTO `grammar_lessons` VALUES (1,'Câu khẳng định, phủ định, nghi vấn và sở thuộc','icon_all.png','2025-12-20 13:14:43','2025-12-20 13:14:43'),(2,'Chỉ định, sở hữu và cách trả lời ngắn trong tiếng Nhật','icon_lesson2.png','2025-12-20 13:18:30','2025-12-20 13:18:30'),(3,'Danh từ chỉ nơi chốn, xuất xứ và giá cả','icon_lesson3.png','2025-12-20 13:53:39','2025-12-20 13:53:39'),(4,'Thời gian và cách nói giờ phút trong tiếng Nhật','icon_lesson4.png','2025-12-20 14:12:24','2025-12-20 15:49:34'),(5,'Di chuyển, phương tiện và từ để hỏi thời gian','train.png','2025-12-20 16:05:41','2025-12-20 16:05:41'),(6,'Động từ, đối tượng và cách mời gọi (bài 6)','lesson6.png','2025-12-20 16:13:10','2025-12-20 16:13:10'),(7,'Phương tiện, cho/nhận và trạng thái hoàn thành (bài 7)','lesson7.png','2025-12-20 16:18:29','2025-12-20 16:18:29'),(8,'Tính từ, mức độ và cách hỏi ý kiến (bài 8)','lesson8.png','2025-12-20 16:32:58','2025-12-20 16:32:58'),(9,'Chủ ngữ, đối tượng với が; mức độ; nguyên nhân và lý do (bài 9)','lesson9.png','2025-12-20 16:44:53','2025-12-20 16:44:53'),(10,'Sự tồn tại, vị trí và cách hỏi về sự hiện hữu (bài 10)','lesson10.png','2025-12-20 16:46:14','2025-12-20 16:46:14'),(11,'Cách đếm, đơn vị đếm và cách hỏi số lượng (bài 11)','lesson11.png','2025-12-20 17:21:48','2025-12-20 17:21:48'),(12,'Thời quá khứ, so sánh hơn và nhất; hỏi lựa chọn (bài 12)','lesson12.png','2025-12-20 17:23:06','2025-12-20 17:23:06'),(13,'Mong muốn, ý muốn làm, mục đích di chuyển và cách hỏi (bài 13)','lesson13.png','2025-12-20 17:24:47','2025-12-20 17:24:47'),(14,'Biến đổi động từ: nhóm, thể て và các mẫu liên quan (bài 14)','lesson14.png','2025-12-20 17:26:10','2025-12-20 17:26:10'),(15,'Cho phép, cấm đoán, trạng thái tiếp diễn và biết/không biết (bài 15)','lesson15.png','2025-12-20 17:27:18','2025-12-20 17:27:18'),(16,'Nối hành động, nối tính từ và hỏi cách/chọn (bài 16)','lesson16.png','2025-12-20 17:28:23','2025-12-20 17:28:23'),(17,'Thể ない, cấm đoán, nghĩa vụ và chủ đề đối tượng (bài 17)','lesson17.png','2025-12-20 17:29:19','2025-12-20 17:29:19'),(18,'Thể từ điển, khả năng, sở thích và các mẫu thời gian (bài 18)','lesson18.png','2025-12-20 17:30:31','2025-12-20 17:30:31'),(19,'Thể た, kinh nghiệm, liệt kê hành động và sự thay đổi (bài 19)','lesson19.png','2025-12-20 17:32:17','2025-12-20 17:32:17'),(20,'Thể văn lịch sự và thể văn thông thường; hội thoại thân mật (bài 20)','lesson20.png','2025-12-20 17:34:10','2025-12-20 17:34:10'),(21,'Thể văn thông thường: とおもいます / といいます / でしょう và các mẫu liên quan (bài 21)','lesson21.png','2025-12-20 17:36:47','2025-12-20 17:36:47'),(22,'Mệnh đề định ngữ và Vる + じかん/やくそく/ようじ (bài 22)','lesson22.png','2025-12-20 17:37:54','2025-12-20 17:37:54'),(23,'Mệnh đề trạng với とき, điều kiện と và động từ chỉ chuyển động (bài 23)','lesson23.png','2025-12-20 17:40:07','2025-12-20 17:40:07'),(24,'Cho/nhận hành động: くれます・あげます・もらいます và mẫu て＋～ (bài 24)','lesson24.png','2025-12-20 17:45:20','2025-12-20 17:45:20'),(25,'Mệnh đề điều kiện: ～たら、～ても、もし/いくら và trợ từ が trong mệnh đề phụ (bài 25)','lesson25.png','2025-12-20 17:46:40','2025-12-20 17:46:40'),(26,'Yêu cầu lịch sự, hỏi lời khuyên, giải thích bằng ～んです và nhấn mạnh chủ đề (bài 26)','lesson26.png','2025-12-20 17:48:55','2025-12-20 17:48:55'),(27,'Thể khả năng, 見える/聞こえる, できます và trợ từ nhấn mạnh (bài 27)','lesson27.png','2025-12-20 17:51:10','2025-12-20 17:51:10'),(28,'Đồng thời hành động, thói quen, liệt kê lý do và liên từ (bài 28)','lesson28.png','2025-12-20 17:53:28','2025-12-20 17:53:28'),(29,'Vています: trạng thái kết quả, Vてしまう, ありました và どこかで/どこかに (bài 29)','lesson29.png','2025-12-20 17:54:32','2025-12-20 17:54:32'),(30,'Vてあります / Vておきます / Vています (trạng thái kết quả) và các mẫu liên quan (bài 30)','lesson30.png','2025-12-20 17:55:38','2025-12-20 17:55:38'),(31,'Thể ý chí, dự định và biểu đạt ý định (bài 31)','lesson31.png','2025-12-20 17:56:36','2025-12-20 17:56:36'),(32,'Lời khuyên, phỏng đoán và biểu đạt khả năng; từ để hỏi + か; giới hạn bằng で (bài 32)','lesson32.png','2025-12-20 17:57:33','2025-12-20 17:57:33'),(33,'Thể mệnh lệnh, cấm đoán, trích dẫn và nhờ truyền lời (bài 33)','lesson33.png','2025-12-20 17:58:44','2025-12-20 17:58:44'),(34,'Quan hệ trước-sau: ～たあとで / ～てから / ～て / ～ないで (bài 34)','lesson34.png','2025-12-20 17:59:37','2025-12-20 17:59:37'),(35,'Thể điều kiện ～ば、なら và cấu trúc liên quan (bài 35)','lesson35.png','2025-12-20 18:00:34','2025-12-20 18:00:34'),(36,'Mục đích, biến đổi khả năng, nỗ lực và ví dụ (～ように / ～ようにする / ～なくなる / ～とか) (bài 36)','lesson36.png','2025-12-20 18:01:39','2025-12-20 18:01:39'),(37,'Động từ dạng bị động, cách dùng và các mẫu liên quan (bài 37)','lesson37.png','2025-12-20 18:02:40','2025-12-20 18:02:40'),(38,'Danh từ hóa động từ bằng の、のは/のが、忘れる (bài 38)','lesson38.png','2025-12-20 18:03:29','2025-12-20 18:03:29'),(39,'Nguyên nhân và lý do: ～て/で、～ので、途中で và các lưu ý (bài 39)','lesson39.png','2025-12-22 10:21:56','2025-12-22 10:21:56'),(40,'Câu hỏi nhúng, の/か/かどうか, Vてみます và danh từ hóa tính từ bằng ～さ (bài 40)','lesson40.png','2025-12-22 10:28:41','2025-12-22 10:28:41'),(41,'Các cách diễn đạt cho và nhận (あげる/やる/さしあげる/もらう/いただく/くださる) và cách nhờ vả lịch sự (bài 41)','lesson41.png','2025-12-22 10:53:47','2025-12-22 10:53:47'),(42,'Diễn đạt mục đích: ～ために / ～ように / ～のに / ～に行く / 数詞＋は・も (bài 42)','lesson42.png','2025-12-22 11:07:59','2025-12-22 11:07:59'),(43,'Diễn đạt suy đoán bằng ～そう、Vてくる và các dạng liên quan (bài 43)','lesson43.png','2025-12-22 11:21:54','2025-12-22 11:21:54'),(44,'Mức độ quá mức, dễ/khó, biến đổi trạng thái và chuyển đổi tính từ (bài 44)','lesson44.png','2025-12-22 11:29:45','2025-12-22 11:29:45'),(45,'～場合は / ～のに (tình huống giả định và sự trái ngược) (bài 45)','lesson45.png','2025-12-22 11:33:01','2025-12-22 11:33:01'),(46,'進行・時点の表現：～ところ／～たばかり／～はずです (bài 46)','lesson46.png','2025-12-22 11:37:54','2025-12-22 11:37:54'),(47,'Truyền đạt thông tin, suy đoán: ～そうです（伝聞）/ ～ようです / ～はず / ～だろう / 感覚表現 (bài 47)','lesson47.png','2025-12-22 11:43:39','2025-12-22 11:43:39'),(48,'Động từ dạng sai khiến (使役形) — tạo dạng, cấu trúc và cách dùng (bài 48)','lesson48.png','2025-12-22 11:45:54','2025-12-22 11:45:54'),(49,'敬語（けいご）: 尊敬語 (tôn kính ngữ) — nguyên tắc, động từ kính ngữ và cách dùng (bài 49)','lesson49.png','2025-12-22 11:51:51','2025-12-22 11:51:51'),(50,'敬語（けいご）: 謙譲語 (khiêm nhường ngữ) và 丁寧語 (từ ngữ lịch sự) — nguyên tắc và ví dụ (bài 50)','lesson50.png','2025-12-22 11:55:48','2025-12-22 11:55:48');
/*!40000 ALTER TABLE `grammar_lessons` ENABLE KEYS */;
UNLOCK TABLES;

--
-- Table structure for table `grammar_pattern`
