DROP TABLE IF EXISTS `grammar_pattern`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `grammar_pattern` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `example` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `exampleMeaning` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `meaning` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pattern` varchar(300) COLLATE utf8mb4_unicode_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=61 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `grammar_pattern`
--

LOCK TABLES `grammar_pattern` WRITE;
/*!40000 ALTER TABLE `grammar_pattern` DISABLE KEYS */;
INSERT INTO `grammar_pattern` VALUES (1,'山田（やまだ）さんは日本語（にほんご）が上手（じょうず）です。','Anh Yamada giỏi tiếng Nhật','thì, là, ở','~ 　は　～'),(2,'きです。私も同じですあなたの家には犬が９もいるんですか？','Nhà bạn có tới 9 con chó luôn à!','cũng, đến mức, đến cả','～も～'),(3,'はしで食べます。','Tôi ăn bằng đũa','tại, ở, vì, bằng, với ( khoảng thời gian)','~ で~'),(4,'午後６時に帰ります','6h chiều tôi sẽ về','chỉ hướng, địa điểm, thời điểm','~　に/ へ ~'),(5,'私は２月一日に日本にきました','Tôi ( đã) đến Nhật vào ngày 1 tháng 2.','vào, vào lúc','~ に ~'),(6,'日本語を勉強します','Tôi học tiếng Nhật','chỉ đối tượng của hành động','~ を　~'),(7,'同僚　とｱﾒﾘｶへ出張　します。','Tôi đi công tác ở Mỹ cùng với đồng nghiệp','với','~　と ~'),(8,'ﾏｲさんに本を貸しました。','Tôi cho Mai mượn sách','cho ~, từ ~','~ に ~'),(9,'休みの日は土曜日と日曜日です。','Ngày nghỉ là thứ bảy và chủ nhật','và','~と~'),(10,'雨が降っています.','Trời đang mưa','nhưng','~ が~'),(11,'大阪から東京まで３時間かかります。','Từ Tokyo đến Osaka mất 3 tiếng.','từ ~ đến ~','~ 　から　~'),(12,'今日の天気はあまり寒くないです。','Thời tiết hôm nay không lạnh lắm','không ~ lắm','～あまり～ない　～'),(13,'全然分かりません','Tôi hoàn toàn không hiểu.','hoàn toàn ~ không.','～　全然～ない　～'),(14,'この問題はなかなか解けない。','Vấn đề này không thể giải quyết ngay được.','mãi mà, mãi mới','～なかなか～ない'),(15,'いっしょに旅行へ行きませんか？','Anh/ chị đi du lịch cùng tôi không?','Anh/ chị cùng …. với tôi không?','～　ませんか～'),(16,'１ｷﾛくらい行くと、ｽｰﾊﾟｰがあります','Đi khoảng 1km thì sẽ có siêu thị','Có','～があります～'),(17,'Lớp học này có 25 người','ﾍﾞﾄﾅﾑには珍しい動物がいます。','Có','～がいます～'),(18,'外国人の社員がいます。','Có 2 nhân viên nước ngoài','Tương ứng với động từ chỉ số lượng','～　動詞＋　数量　～'),(19,'私は　一週間に４回日本語を勉強します。','Tôi học tiếng nhật 4 ngày 1 tuần','Làm ~ lần trong khoảng thời gian.','～に～回'),(20,'では、終わりましょう。','Chúng ta kết thúc thôi','chúng ta hãy cùng ~','～ましょう～'),(21,'重いですね。待ちましょうか？','Nặng nhỉ, để tôi mang giúp cho bạn','Tôi ~ hộ cho anh nhé','～　ましょうか？～'),(22,'私は友達が欲しいです。','Tôi muốn có bạn bè','Muốn','～　がほしい'),(23,'寿司を食べたいです。','Tôi muốn ăn sushi','Muốn','～たい～'),(24,'ｽｰﾊﾟｰへ買い物に行きます','Tôi đi siêu thị để mua sắm','Đi đến….để làm gì','～へ～を～に行'),(25,'ここに住所と名前を書いてください。','Xin hãy viết địa chỉ và tên vào chỗ này','Hãy','～てください～'),(26,'ここで写真を撮らないでください。','Xin đừng chụp ảnh ở đây','( xin ) đừng / không','ないでください'),(27,'ﾀﾊﾞｺを吸ってもいいですか?','Tôi hút thuốc có được không?','Làm ~ được:','てもいいです'),(28,'ここでﾀﾊﾞｺを吸ってはいけませんか?いいえ。吸ってはいけません','Tôi hút thuốc ở đây có được không?Không, không được hút','Không được làm ~','てはいけません'),(29,'しなくてもいいです。','Không làm cũng được','Không phải, không cần ~ cũng được','なくてもいいです'),(30,'毎日一時間日本語を勉強しなければなりません','Mỗi ngày tôi phải học tiếng Nhật 1 tiếng','Phải ~','なければなりません'),(31,'卒業に合格ために、一生懸命勉強しないといけない。','Tôi phải học hành chăm chỉ để đậu tốt nghiệp','Phải ~','ないといけない'),(32,'１０時前に寝なくちゃいけない。','Tôi phải ngủ trước 10h','Không thể không (phải)','なくちゃいけない'),(33,'あの人にだけは負けたくない。','Thua ai khác thì được, chứ tôi không muốn thua anh ấy','Chỉ ～','～だけ～'),(34,'毎朝、ﾆｭｰｽを見ませんか?','Anh có xem tin tức vào buổi sáng không?','Vì ～','～から～'),(35,'彼女は日本語を話すのが上手です。','Cô ấy nói tiếng Nhật giỏi','Danh từ hóa động từ','～のが～'),(36,'新先生のめいさんが名前のを知っていますか?','Bạn có biết cô giáo mới tên Mei không?','Danh từ hóa động từ','～のを～'),(37,'いっしょに食事のは楽しいです。','Cùng nhau ăn uống thì thật là vui','Danh từ hóa động từ','～のは～'),(38,'もう晩御飯を食べましたか?','Đã ăn tối chưa?','Đã làm gì ~','もう～ました'),(39,'この本は、まだ読んでいませんか?　いいえ、まだです。','Cuốn sách này đọc chưa? Vẫn chưa đọc','Vẫn chưa làm ~','まだ～ていません'),(40,'今年の冬は昨年よりも寒い。','Mùa đông năm nay lạnh hơn mùa đông năm ngoái','So với…','～より～'),(41,'山田さんは田中さんほど英語を話するが上手ではありません。','Anh Yamada nói tiếng Anh không giỏi bằng Anh Tanaka','Không … bằng','ほど～ない'),(42,'あの人が食べているのと同じものをください。','Cho tôi món giống như món của người kia đang ăn.','Giống với ~, tương tự với ~','～と同じ～'),(43,'３人姉妹のなかで、私が一番細いです。','Trong số 3 chị em thì tôi là người ốm nhất','Trong số … nhất','のなかで～がいちばん'),(44,'今部屋はもっと暖かくなる。','Căn phòng này trở nên ấm hơn','Trở thành, trở nên','く/ ~ になる'),(45,'お腹がすいた、食べません。','Cho dù đói cũng không ăn','Cho dù ~ cũng không','～も～ない～'),(46,'このかばん大きかったり、重かったりするかばん。','Cái cặp này to và nặng','Làm ~ làm, và ~','～たり~たりする'),(47,'妹は高学校で勉強しています。','Em gái tôi đang học cấp 3','Vẫn đang','～ている～'),(48,'私は月に何回ｽｰﾊﾟｰに行くことがあります。','Một tháng tôi đi siêu thị vài lần','Có khi, thỉnh thoảng','～ることがある～'),(49,'勉強にいてどこへも行かないことがありませんか?','Bạn có khi nào học suốt mà không đi đâu không?','Có khi nào không….?','ないことがある'),(50,'これだけ練習していても、時として失敗することがある。','Luyện tập nhiều như vậy nhưng cũng có lúc thất bại.','Đã từng','たことがある'),(51,'袋の中にお金や写真などがあります。','Trong túi có tiền và hình.','Như là…và…','～や～など'),(52,'もう遅いのでこれで失礼いたします。','Vì đã muộn nên tôi xin phép về trước','Bởi vì ~','～ので～'),(53,'結婚するまえには、一度ゆっくり仲間と旅行てもしてみたい。','Trước khi lập gia đình, tôi muốn cùng đám bạn thân đi du lịch thỏa thích một lần','trước khi ~','～まえに～'),(54,'国へ帰ってから、大学で働きます。','Sau khi về nước tôi làm ở trường đại học','Sau khi ~, từ khi ~','～てから'),(55,'仕事のあとで、カラオケにいきませんか?','Sau khi xong việc thì đi hát karaoke không?','Sau khi ~','～たあとで'),(56,'東京へ行くとき夜行ﾊﾞｰｽを使っていった。','Lúc đi Tokyo tôi đã sử dụng xe buýt chạy đêm','Khi ~','～とき'),(57,'あなたは、学生さんでしょうか?','Bạn là sinh viên, đúng không?','~ đúng không?','～でしょう?～'),(58,'夏休みに田舎へ帰らないでしょう!','Nghĩ hè này chắc không về quê.','Chắc hẳn là ~, có lẽ ~','多分～でしょう'),(59,'確か、机の上に置いたともいます。','Tôi nhớ là mình đã đặt trên bàn.','Tôi nghĩ rằng','～と思います'),(60,'彼は「その子を妹だ」と言います','Anh ấy nói cô bé kia là em gái của mình','Nói ~','～と言います');
/*!40000 ALTER TABLE `grammar_pattern` ENABLE KEYS */;
UNLOCK TABLES;

--
-- Table structure for table `grammar_points`
