  `character` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `romanji` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL,
  `unicode` varchar(10) COLLATE utf8mb4_unicode_ci NOT NULL,
  `stroke_order` int DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  UNIQUE KEY `character` (`character`),
  UNIQUE KEY `character_2` (`character`),
  UNIQUE KEY `character_3` (`character`),
  KEY `idx_katakana_char` (`character`),
  KEY `idx_katakana_romanji` (`romanji`)
) ENGINE=InnoDB AUTO_INCREMENT=105 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
