  `id` bigint NOT NULL AUTO_INCREMENT,
  `user_id` bigint NOT NULL COMMENT 'Tham chiếu đến users.id (BIGINT)',
  `lesson_id` int NOT NULL COMMENT 'Tham chiếu đến grammar_lessons.id (INT)',
  `answers` json NOT NULL COMMENT 'Câu trả lời của user, lưu dạng JSON (ví dụ: [{"question_id": 1, "user_answer": "です"}] )',
  `time_spent` int DEFAULT '0' COMMENT 'Thời gian làm bài (giây)',
  `submitted_at` datetime DEFAULT CURRENT_TIMESTAMP COMMENT 'Thời gian nộp bài',
  `feedback` text COLLATE utf8mb4_unicode_ci COMMENT 'Feedback từ admin',
  `feedback_at` datetime DEFAULT NULL COMMENT 'Thời gian admin gửi feedback',
  `status` enum('pending','feedbacked') COLLATE utf8mb4_unicode_ci DEFAULT 'pending',
  `score` int DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_user_id` (`user_id`),
  KEY `idx_lesson_id` (`lesson_id`),
  KEY `idx_submitted_at` (`submitted_at` DESC),
  KEY `idx_status` (`status`),
  KEY `idx_mini_test_score` (`score`),
  CONSTRAINT `fk_mini_test_submissions_lesson` FOREIGN KEY (`lesson_id`) REFERENCES `grammar_lessons` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `fk_mini_test_submissions_user` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
