INSERT INTO `question` VALUES (11,'D',1,'がくせい = học sinh/sinh viên → Karina là sinh viên.','まで','みなさん','びょういん','がくせい','カリナさんは　＿　です。',1),(12,'A',2,'は = trợ từ chủ ngữ → Wang-san là bác sĩ.','は','を','に','で','ワンさん　＿　いしゃです。',1),(13,'A',3,'でんき = điện → Miller-san là nhân viên công ty điện.','でんき','ぎんこういん','せんせい','わたしたち','ミラーさんは IMC 　＿　しゃいんです。',1),(14,'A',4,'Cấu trúc \"AもBです\" = cả A lẫn B đều... → Santos-san cũng là nhân viên công ty.','も','に','は','を','ミラーさんはかいしゃいんです。サントスさん　＿　かいしゃいんです。',1),(15,'A',5,'はじめまして = lần đầu gặp → câu chào khi tự giới thiệu.','はじめまして','から','まで','びょういん','わたしはミラーさんです。＿　どうぞよろしくおねがいします。',1),(16,'A',6,'Miller-san không phải kỹ sư mà là nhân viên công ty.','かいしゃいん','おはようございます','きょうしつ','でんき','ミラーさんはエンジニアですか。．．．いいえ、エンジニアじゃありません。＿　です。',1),(17,'B',7,'どなた = ai (lịch sự) → Hỏi \"Người kia là ai vậy?\"','さくらだいがく','どなた','なんさい','はじめまして','あのかたは　＿　ですか。',1),(18,'C',8,'ほん = sách → \"Đây là sách.\"','これ','あれ','ほん','あなた','これは　＿　です。',1),(19,'B',9,'コンピューター = máy tính → \"Đây là sách của máy tính.\" (sách về máy tính)','じしょ','コンピューター','これ','ざっし','これは　＿　のほんです。',1),(20,'D',10,'しんぶん = báo → \"Đó là báo của tôi.\"','これ','あれ','どれ','しんぶん','それはわたしの　＿　です。',1),(21,'C',1,'なに = gì → hỏi \"Đây là gì vậy?\"','テレホンカード','どなた','なに','そう','これは ＿　ですか。',2),(22,'A',2,'てちょう = sổ tay → phủ định \"không phải sổ ghi chép\" thì là sổ tay','てちょう','どうぞ','こちらこそよろしく','にほんご','それはノートですか。．．．いいえ、そうじゃありません。＿　です。',2),(23,'C',3,'しんぶん = báo → hỏi \"Đó là báo của ai?\"','あなた','わたし','しんぶん','なんに','あれはだれの　＿　ですか。',2),(24,'D',4,'えんぴつ = bút chì → \"Đây là bút chì của Sato-san\"','えいご','にほんご','フランス語','えんぴつ','これはさとうさんの　＿　です。',2),(25,'D',5,'わたし = tôi → \"Đây là chìa khóa của tôi\"','てちょう','しんぶん','どなた','わたし','このかぎはだれのですか。　＿　のです。',2),(26,'B',6,'なに = gì → hỏi \"Đây là gì?\"','ポールペン','なに','これ','どうぞ','これは　＿　ですか。',2),(27,'D',7,'シャープペンシル = bút chì bấm → hỏi lựa chọn giữa bút bi và bút chì bấm','しんぶん','テレホンカード','かぎ','シャープペンシル','それはボールペンですか、　＿　ですか。',2),(28,'D',8,'じどうしゃ = ô tô → \"Đây là sách về ô tô\"','とけい','かぎ','かさ','じどうしゃ